import Card from './Card/Card';
import Button from '../Button/Button';
import './CardBody.css';

class CardBody extends Component {
    render (){

    const diseño = 'Diseño';
    const entrega = 'Entrega';
    const disfruta = 'Disfruta';

    const uno = '1';
    const dos = '2';
    const tres = '3';

    const disCont = 'Tu diseñador personal te contactara para definir el estilo, preferencias y estilo.';
    const entCont = 'Te enviaremos un plan de entrega basado en tu diseño ysemanal, quinc.';
    const disfCont = 'Tu diseñador personal te contactara para definir el estilo, preferencias y estil.';

    const estilo = 'center luz br4  mv3 mh3 ba b--black-10 w-30-l w-100 ';
    return (

        <div className='vh-100'>
            <div id='proceso' className='flex justify-center-ns flex-wrap items-center w-75 center mv5 vh-50'>
                <Card text = {diseño} number = {uno} content = {disCont} estilo = {estilo} /> 
                <Card text = {entrega}  number = {dos} content = {entCont} estilo = {estilo}/>
                <Card text = {disfruta}  number = {tres} content = {disfCont} estilo = {estilo}/>
            </div>
            <div className='flex flex-column center'>
                <div >
                    <Button text={this.props.gretting} estilo={this.props.estiloButton}/>
                </div>
                <div className='tc flex center justify-center'>
                    <a href="#h" className="arrow"> 
                        <svg className="icon icon-arrow-down" width="60" height="60" viewBox="0 0 32 32"> 
                            <g id="icon-arrow-down">
                                <path className="path1" d="M4.131 8.962c-0.434-0.429-1.134-0.429-1.566 0-0.432 0.427-0.432 1.122 0 1.55l12.653 12.528c0.434 0.429 1.133 0.429 1.566 0l12.653-12.528c0.432-0.429 0.434-1.122 0-1.55s-1.136-0.429-1.566-0.002l-11.87 11.426-11.869-11.424z" 
                                fill="#B60D35" 
                                ></path>
                            </g>
                        </svg> 
                    </a>
                </div>
            </div>
        </div>




    );

    }
    }

    export default CardBody;
