        const gretting = 'Hablemos';

        const estiloButton1 = 'br3-ns br3 ba color-boton1 ttu white center pt2 pb2 pr4 pl4 f4 f2-ns no-underline grow';
        const estiloButton2 = 'br3-ns br3 ba color-boton2 ttu white center pt2 pb2 pr4 pl4 f4 f2-ns no-underline grow';


    return (
        <div className='tc-l vh-100 flex flex-column center justify-center '>
            <div className='tc pb5-l pb1 pt5-l pt1 flex justify-center align-items vh70'>
                <div className='ma5 w-50 pa4 ba b--none br4 a flex flex-column justify-center vh-50 luz'>
                    <h1 className='f2 f1-ns fw1 ttu black-90 mb0 pa0'>Descrubre como te <br/> puede ayudar  <br/> la Web</h1>
                    <h2 className='fw1 f3-ns f6 fw2 black-80 mt3 mb4'>Revisa nuestra guia o escribemos</h2>

                    <div className='center flex flex-wrap'>
                        <div  className='center mb3 pr4' >
                            <Button text = {gretting1}  estilo = {estiloButton1} onSumbitSubscription = {this.onSumbitSubscription} />
                        </div>
                        <div  className='center mb3 pl4' >
                            <Button text = {gretting}  estilo = {estiloButton2} onSumbitSubscription = {this.onSumbitSubscription} />
                        </div>    
                    </div>
                </div>
            </div>
            <div className='tc flex center justify-center'>
                <p className='fw1 f3 f2-ns lh-title'>Â¿Que Problemas tienes?</p>
                <a href="#problemas" className="arrow"> 
                    <svg className="icon icon-arrow-down" width="60" height="60" viewBox="0 0 32 32"> 
