import React from 'react';
import Nav from './Components/Nav/Nav';
import Problemas from './Components/Problemas/Problemas';
import HeroWindow from './Components/HeroWindow/HeroWindow';
import Process from './Components/Process/Process';
import Nosotros from './Components/Nosotros/Nosotros'
import CallTo from './Components/CallTo/CallTo';
import './App.css';
import Fracaso from './Components/Fracaso/Fracaso';
import Exito from './Components/Exito/Exito';
import Sidebar from './Components/Sidebar/Sidebar';
import { slide as Menu } from "react-burger-menu";

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      route: 'home',
      menuOpen: false
    }
  }

  onRouteChange = (route, menuOpen) => {
    this.setState({route: route});
    console.log(route)
  }


  // This keeps your state in sync with the opening/closing of the menu
  // via the default means, e.g. clicking the X, pressing the ESC key etc.
  handleStateChange (state) {
    this.setState({menuOpen: state.isOpen})  
  }

  // This can be used to close the menu, e.g. when a user clicks a menu item
  closeMenu () {
    this.setState({menuOpen: false})
  }

  componentBeforeMount() {
    window.scrollTo(0, 0);
  }

  render(){

    const gretting = 'Hablemos';
    const estiloButton = 'br3-ns br3 ba color-boton2 ttu white center pt2 pb2 pr4 pl4 f5 f3-ns no-underline grow';
    const isOpen = this.state.menuOpen;


    return(

      <div>

          <Sidebar onRouteChange = {this.onRouteChange} onClickChange = {isOpen}/>
          <Menu

          width={ '50%' }
          right
          isOpen={this.state.menuOpen}
          onStateChange={(state) => this.handleStateChange(state)}
          >

      <br></br>
      <br></br>
      <br></br>
      <br></br>

      <div >
        <a href="#home" ><p className='hover-it grow pointer tc  f3 f2-m f1-l ma2' onClick={this.onRouteChange('home')}>Home</p></a>
        <a href="#nosotros" ><p className='hover-it grow pointer tc  f3 f2-m f1-l ma2' onClick={this.onRouteChange('nosotros')}>Nosotros</p></a> 
        <a href="#calltoaction" ><p className='hover-it grow pointer tc  f3 f2-m f1-l ma2' onClick={this.onRouteChange('contacto')}>Contacto</p></a> 
        <a href="https://yonkeenn.com" target="_blank" rel='noreferrer' ><p className='hover-it grow pointer tc  f3 f2-m f1-l ma2' onClick={this.onRouteChange('blog')}>Blog</p></a> 

      </div>


      <div className="o-50">

        <div className="flex flex-wrap  items-center justify-center  mt4">

          <div className="flex flex-column w-50-l center">

              <div className="flex flex-column  items-center justify-center mb4-l cf tc center mt4">
              <h1 className="f3 f2-m f1-l ma0  lh-solid">Contácto</h1>
              <article className="fl w-100 dib-ns w-auto-ns  mt4 tc">
                <a className=" link hover-silver near-white dib mv1 mh2 tc" href="https://instagram.com/yamheru" target="_blank" rel='noreferrer'>
                  <i className="fab fa-instagram f2"></i>
                </a>
                <a className="link hover-silver near-white dib mv1 mh2 tc" href="https://facebook.com/yamheru" target="_blank" rel='noreferrer'>
                    <i className="fab fa-facebook-square f2"></i>
                </a>
                <a className="link hover-silver near-white dib mv1 mh2 tc" href="https://wa.me/51948018337?text=Hola%2C+Yamheru%2C+Estoy+interesado+en+sus+diseños+florales" target="_blank" rel='noreferrer'>
                    <i className="fab fa-whatsapp f2"></i>
                </a>



              </article>

              <article className="fl w-100 dib-ns w-auto-ns mt3  tc">
                <a href="tel:+51-902551938" className="f6 db fw1 pv2 link dim white" title="Call Lima office.">
                  +51-948018337
                </a>
              </article>

              <article className="fl w-100 dib-ns w-auto-ns  tc">
                <a href="mailto:hola@yamheru.com" className="f6 db fw1 pv2 link dim white" title="Mail office.">hola@yamheru.com</a>
              </article>

            </div>
          </div>

        </div>
      </div>


      <div className="o-50 tc dt dt--fixed w-100 flex flex-column" >

        <div className="">
          <p className="f7 black-70 dib mb3 white">
            © iTresse 2021
          </p>
        </div>

      </div>


    </Menu>
          {
            this.state.route === 'nosotros' 
              ? (<div>
                <Nosotros />
                <CallTo gretting={gretting} estiloButton={estiloButton}/>
              </div>)
              : (<div>
                  <HeroWindow gretting={gretting} estiloButton={estiloButton}/>
                  <Problemas />
                  <Process gretting={gretting} estiloButton={estiloButton}/>
                  <Fracaso />
                  <Exito />
                  <CallTo gretting={gretting} estiloButton={estiloButton}/>
                </div>)


          }
      </div>

    );
  }



}

export default App;
