import React, { Component } from 'react';
import brochure from './brochure.png'

class Nosotros extends Component {
    constructor(props){
        super(props);
        this.state = {

        }
    }



    render (){

        const gretting = 'Hablemos';
        const estiloButton = 'br3-ns br3 ba color-boton2 ttu white center pt2 pb2 pr4 pl4 f5 f3-ns no-underline grow';


    return (
        <section id='problemas' className='img-back1 pt6-ns pv5 pb3 flex flex-column justify-center align-center tc ' >

            <div className="center w-90 ph3 ph5-l tc br4-ns pv3 flex flex-column flex-wrap justify-center">

                <div className='flex items-center content-between  br3 w-90-l grow vh-50  center  luz mv1 ph4' >
                    <div className='ph2-ns w-70-l'>
                        <h1 className="fw2 f5 f3-ns ttu lh-title mt0 mb3">
                            Nosotros
                        </h1>
                        <h2 className="fw2 f6 f5-ns lh-copy-ns mt0 mb3">
                        Somos Itresse, es una Start-Up que se enfoca en la creacion de estrategias de marketing digital bajo el enfoque de Lean UX, Agile. En el 2020, debido a la coyuntura del COVID.29, hemos innovado creando un producto que une UX y ML. Nuestra propuesta se resume en el siguiente cuadro. En la fase inicial de cada producto digital, es importante el enfoque en el Usuario que vive el dia a dia, por lo que es importante conocer el journey que tiene en el proceso de compra. Luego esta la experimentacion del prototipo funcional con caracteristicas basicas que generan valor al negocia. Y la parte final esta la mejora continua con ML(Machine Learning) generando insights.
                        </h2>
                    </div>
                    <div className='w-30-l w-100 ma0 pa0  dn-s' >
                        <img
                            src={brochure}
                            className='w-100 vh-20 db pa0 ma0 br4 br--right b-white'
                            alt='of a kitty staring at you'
                            />
                    </div>
                </div>

                <div className='flex items-center content-between  br3 w-90-l grow vh-50 center  luz mv1 ph4' >
                    <div className='w-30-l w-100 ma0 pa0 dn-s' >
                        <img
                            src={brochure}
                            className='w-100 vh-20 db pa0 ma0 br4 br--left'
                            alt='of a kitty staring at you'
                            />
                    </div>
                    <div className='ph2-ns'>
                        <h1 className="fw2 f5 f3-ns ttu lh-title mt0 mb3">
                            Historia
                        </h1>
                        <h2 className="fw2 f6 f5-ns lh-copy-ns mt0 mb3 ">
                        El fundador de esta iniciativa es el candidato a Master of Science en Ciencias de la Computacion en la UNI, Lima-Peru, el Bach. Jhon Vargas Reyes que es quien lidera el grupo. Bajo su experiencia esta liderar equipos de telecomuniaciones bajos el enfoque agil por mas de 10ños. Experiencia en Projectos bajo el enfoque PMI, Metododos Agiles y Desing Thinking. Actualmente cursa la Maestria en Computer Science en la Universidad Nacional de Ingenieria. 
                        </h2>
                    </div>

                </div>


            </div>


        </section>

    );

}
}

export default Nosotros;
