        }
    }

    render (){

    const diseño = 'Diseño';
    const entrega = 'Desarrollo';
    const disfruta = 'Mejoramiento';

    const uno = '1';
    const dos = '2';
    const tres = '3';

    const disCont = 'Envianos tu idea y elaboremos un plan que se adapte a ti';
    const entCont = 'Crearemos tu producto y midamos para validar los resultados.';
    const disfCont = 'Ajustemos acorde los resultados hasta alcanzar tus objetivos.';

    const estilo = 'center luz br4  mv3 mh3 ba b--black-10 w-30-l w-100 ';
    return (

        <div id='proceso' className='vh-100 flex flex-column items-center'>
            <div  className='flex justify-center-ns flex-wrap items-center w-75 center mt6 vh-75'>
                <Card text = {diseño} number = {uno} content = {disCont} estilo = {estilo} /> 
                <Card text = {entrega}  number = {dos} content = {entCont} estilo = {estilo}/>
                <Card text = {disfruta}  number = {tres} content = {disfCont} estilo = {estilo}/>
            </div>
            <div >
                <Button text={this.props.gretting} estilo={this.props.estiloButton}/>
            </div>
        </div>




    );

    }
    }

    export default CardBody;
